void show_board(Board board)
{
    int i,j;
    int w,b;

    if (board.tried == 0) {
        return;
    }

    printf("┌───────────┬──────┐\n");
    printf("│  Guesses  │ Hint │\n");
    printf("├───────────┼──────┤\n");
    for (i = 0; i < board.tried; i++) {
        printf("│");
        print_guesses(board.rounds[i].player_password.password);
        /*printf("   %s   ", board.rounds[i].player_password.password);*/
        printf(" │ ");
        w = calc_pins(board.rounds[i].player_password.password, board.password.password, 'W');
        b = calc_pins(board.rounds[i].player_password.password, board.password.password, 'B');
        for (j = 0; j < w; j++) {
            printf("%sW%s",KWHT,KNIL);
        }
        for (j = 0; j < b; j++) {
            printf("%sB%s",KBLK,KNIL);
        }
        for (j = 0; j < (PL-(w+b)); j++) {
            printf("-");
        }
        printf(" │");
        printf("\n");
    }
    printf("└───────────┴──────┘\n");
}

void print_guesses(char* guesses)
{
    int i;
    int count = 0;

    printf("   ");
    for(i = 0; i < PASSWORD_LENGTH - 1; i++){
        count++;
        switch (guesses[i])
        {
        case 'R':
            printf("%s%c%s",KRED,guesses[i],KNIL);
            break;

        case 'G':
            printf("%s%c%s",KGRN,guesses[i],KNIL);
            break;

        case 'Y':
            printf("%s%c%s",KYEL,guesses[i],KNIL);
            break;

        case 'B':
            printf("%s%c%s",KBLU,guesses[i],KNIL);
            break;

        case 'M':
            printf("%s%c%s",KMAG,guesses[i],KNIL);
            break;

        case 'C':
            printf("%s%c%s",KCYN,guesses[i],KNIL);
            break;
        
        default:
            break;
        }
    }
    printf("   ");
}

void clear_screen()
{
    system("@cls||clear");
}

int choose_menu_option()
{
    int n = 0;
    int option;
    char c;
    char whipeout_stdin;
    
    while(1){
        if(n){while ((whipeout_stdin = getchar()) != '\n' && whipeout_stdin != EOF){}}
        n++;
        printf("Which option will you choose? (1/2/3/4)\n");
        printf("> ");
        c = getchar();
        option = atoi(&c);
        if (option < 1 || option > 4 || ((c = getchar()) != '\n' && c != EOF)) {
            printf("There's something wrong! Can you type again?\n\n");
        } else {
            /* printf("You've chosen option %d.\n", option); */
            break;
        }
    }
    
    return option;
}

void show_instructions()
{
    char whipeout_stdin;
    clear_screen();
    printf("MASTERMIND is a code-breaking game.\n\n");
    printf("- The program itself will generate a password and the player has to guess it's sequence.\n\n");
    
    printf("- The player must break the code and guess the password within the tries given. Each game,\n" 
           "there will be a total of 8 tries and each try they has to choose 4 colors in a total of 6 \n" 
           "available and try to guess the password.\n\n");
            
    printf("- Each time the player tries to guess and fails, both the number of right answers and tries\n" 
           "left will appear on screen.\n\n");
            
    printf("- The player loses the game if there are no more tries left.\n\n");
    
    printf("Available colors: %s R %s G %s Y %s B %s M %s C %s\n", KRED, KGRN, KYEL, KBLU, KMAG, KCYN, KNIL);
    printf("Example of entry (input is case insensitive): rbby or RBBY\n");
    printf("\nPress <enter> to exit the Instructions page.\n");
    while ((whipeout_stdin = getchar()) != '\n' && whipeout_stdin != EOF){}
}


